import { Input, Select, SelectOnChange } from './blocks/Input';
import { TYPES, BRAND_FRIDGE, BRAND_LAPTOP, BRAND_TVSET, BRAND_MOBILE } from './vars';
import { useState } from 'react';

const Registration = () => {
  const types = document.getElementById('types');
  const [type, setType] = useState();
  const getBrand = () => {
    switch(type){
      case 'tv-set': return BRAND_TVSET;
      case 'fridge': return BRAND_FRIDGE;
      case 'laptop': return BRAND_LAPTOP;
      case 'mobile phone': return BRAND_MOBILE; 
      default: return 'choose type'; 
    }
  }


  return (
    <section className='registration'>
      <h1>Live a request</h1>
      <form action='#'>
        <Input id='firstname' name='Name' type='text' />
        <Input id='mobile' name='Mobile' type='text' />
        <SelectOnChange name='types' types={TYPES} getType={setType}/>
        <Select name='brand' types={getBrand()} />
      </form>
    </section>
  );
};

export default Registration;
