import { useState } from 'react';
const App = () => {
  const [ todos, setTodos ] = useState([]);
  const [ text, setText ] = useState();

  const [ box, setBox ] = useState([]);

  const addTodo = () => {

      setTodos([
        ...todos,
        {
          id: new Date().toISOString(),
          text
        }
      ])

  }

  const btnDelete = () => {

  }

  const check = (id) => {
    setBox([
      ...box,
      id
    ]);
  }

  return (
    <>
      <div>
        <input type='text' onChange={e => setText(e.target.value)} />
        <input type='submit' onClick={addTodo} />
      </div>
      <ul>
        {
          todos.map(el=> <li key={el.id}>
            <input type="checkbox" value="check" onClick={()=>check(el.id)}/>
            {el.text} 
            <input type="button" value="delete" onClick={()=>btnDelete()}/>
            </li>)
        }
      </ul>
    </>
  );
};

export default App;
