import createSlice from '-re'

const slice = createSlice({
  name: 'todoSlice',
  initialState: {
    todos: [],
  },
  reducers: {
    addTodo(state, action) {
      console.log(state);
      console.log(action);

      state.todos.push({
        id: new Date().toISOString(),
        text: action.payload.text,
        checked: false,
      });
    },
    btnDelete(state, action) {},
    check(state, action) {},
  },
});

export const { addTodo, btnDelete, check } = slice.actions;
export default slice.reducer;
