import { Select, Input, SelectOnChange } from './blocks/form';
import { TYPES } from '../vars/vars';
import { useSelector } from 'react-redux';
import { FIRSTNAME_VALIDATION, MOBILE_VALIDATION, MODEL_VALIDATION } from '../vars/validations';
import { FIRSTNAME_ERROR, MOBILE_ERROR, MODEL_ERROR, SELECT_ERROR } from '../vars/errors';
import { checkInput, checkSelect } from '../vars/functions';

const RegistrationForm = () => {
  const selector = useSelector(state => state.todos.todos);
  const [name, setFirstnam] = useState(FIRSTNAME_VALIDATION);

  const createBill = e => {
    e.preventDefault();

    const FIRSTNAME = document.getElementById('firstname');
    const FIRSTNAME_BLOCK_ERROR = document.getElementById('firstname_error');
    const MOBILE = document.getElementById('mobile');
    const MOBILE_BLOCK_ERROR = document.getElementById('mobile_error');
    const TYPESSELECT = document.getElementById('types');
    const TYPESSELECT_BLOCK_ERROR = document.getElementById('types_error');
    const BRANDSELECT = document.getElementById('brands');
    const BRANDSELECT_BLOCK_ERROR = document.getElementById('brands_error');
    const MODEL = document.getElementById('model');
    const MODEL_BLOCK_ERROR = document.getElementById('model_error');

    checkInput(FIRSTNAME, FIRSTNAME_VALIDATION, FIRSTNAME_BLOCK_ERROR, FIRSTNAME_ERROR);
    checkInput(MOBILE, MOBILE_VALIDATION, MOBILE_BLOCK_ERROR, MOBILE_ERROR);
    checkInput(MODEL, MODEL_VALIDATION, MODEL_BLOCK_ERROR, MODEL_ERROR);
    checkSelect(TYPESSELECT, 'types', TYPESSELECT_BLOCK_ERROR, SELECT_ERROR);
    checkSelect(BRANDSELECT, 'brands', BRANDSELECT_BLOCK_ERROR, SELECT_ERROR);
    if (
      FIRSTNAME_BLOCK_ERROR.textContent === '' &&
      MOBILE_BLOCK_ERROR.textContent === '' &&
      TYPESSELECT_BLOCK_ERROR.textContent === '' &&
      BRANDSELECT_BLOCK_ERROR.textContent === '' &&
      MODEL_BLOCK_ERROR.textContent === ''
    ) {
      const form = document.getElementById('registrationForm');
      const formData = new FormData(form);
      const formProps = Object.fromEntries(formData);
      const json = JSON.stringify(formProps);

      localStorage.setItem('bill', json);

      document.getElementById('registrationForm').submit();
    }
  };
  return (
    <section className='registration'>
      <h1>Live a request</h1>
      <form action='/form' method='GET' name='registrationForm' id='registrationForm'>
        <Input id='firstname' name='FirstName' type='text' />
        <Input id='mobile' name='Mobile' type='text' />
        <SelectOnChange name='types' types={TYPES} />
        <Select types={selector} name='brands' />
        <Input id='model' name='Model' type='text' />
        <input type='submit' onClick={e => createBill(e)} />
      </form>
    </section>
  );
};

export default RegistrationForm;
