import { useState } from 'react';
import TodoForm from './components/TodoForm';

const App = () => {
  const [text, setText] = useState();


  const btnDelete = () => {
    setTodos(
      todos.filter(el => {
        return el.checked === false;
      })
    );
  };

  const check = (id) => {
    todos.map(el=>{
      if(el.id === id) el.checked = true
    })
  }

  return <TodoForm check = {check} btnDelete={btnDelete} addTodo = {addTodo} setText = {setText}/>;
};

export default App;
