import { useState } from 'react';
const App = () => {
  const [ todos, setTodos ] = useState([]);
  const [ text, setText ] = useState();

  const addTodo = () => {

      setTodos([
        ...todos,
        {
          id: new Date().toISOString(),
          text,
          checked: false
        }
      ])

  }

  const btnDelete = () => {
    con
    todos.filter(el=>{
      return el.checked === true
    })
  }

  const check = (id) => {
    todos.filter(el=>{
      if(el.id === id)  el.checked = true;
    })
  }

  return (
    <>
      <div>
        <input type='text' onChange={e => setText(e.target.value)} />
        <input type='submit' onClick={addTodo} />
      </div>
      <ul>
        {
          todos.map(el=> <li key={el.id}>
            <input type="checkbox" value="check" onClick={()=>check(el.id)}/>
            {el.text} 
            <input type="button" value="delete" onClick={()=>btnDelete()}/>
            </li>)
        }
      </ul>
    </>
  );
};

export default App;
