import { Input, Select, SelectOnChange } from './blocks/Input';
import { TYPES, BRAND_FRIDGE, BRAND_LAPTOP, BRAND_TVSET, BRAND_MOBILE } from './vars';
import { useState } from 'react';

const Registration = () => {
  const types = document.getElementById('types');
  const [type, setType] = useState();
  const getBrand = () => {
    let result = [];
    switch (type) {
      case 'tv-set':
        result = BRAND_TVSET;
        break;
      case 'fridge':
        result = BRAND_FRIDGE;
        break;
      case 'laptop':
        result = BRAND_LAPTOP;
        break;
      case 'mobile phone':
        result = BRAND_MOBILE;
        break;
      default:
        result = [];
    }
    return result;
  };

  return (
    <section className='registration'>
      <h1>Live a request</h1>
      <form action='#'>
        <Input id='firstname' name='FirstName' type='text' />
        <Input id='mobile' name='Mobile' type='text' />
        <SelectOnChange name='types' types={TYPES} getType={setType} />
        <Select types={getBrand()} name='brand' />
        <Input id='mode' name='Mark' type='text' />
      </form>
    </section>
  );
};

export default Registration;
