import { createSlice } from '@reduxjs/toolkit'

const slice = createSlice({
  name: 'todoSlice',
  initialState: {
    todos: 0,
  },
  reducers: {
    getBrand(state,action){
      let result = [];
      switch (type) {
        case 'tv-set':
          result = BRAND_TVSET;
          break;
        case 'fridge':
          result = BRAND_FRIDGE;
          break;
        case 'laptop':
          result = BRAND_LAPTOP;
          break;
        case 'mobile phone':
          result = BRAND_MOBILE;
          break;
        default:
          result = [];
      }
      return result;
    },
    increment(state,action){
      state.todos+=action.payload
    },
    addTodo(state, action) {
      console.log(state.todos.push);
      // console.log(action);

      state.todos.push({
        id: new Date().toISOString(),
        text: action.payload,
        checked: false,
      });
      console.log(state.todos)
    },
    btnDelete(state, action) {},
    check(state, action) {},
  },
});

export const { addTodo, btnDelete, check, increment } = slice.actions;
export default slice.reducer;
