import { createSlice } from '@reduxjs/toolkit'

const slice = createSlice({
  name: 'todoSlice',
  initialState: {
    todos: 0,
  },
  reducers: {
    increment(state,a){
      count+=1;
    },
    addTodo(state, action) {
      console.log(state.todos.push);
      // console.log(action);

      state.todos.push({
        id: new Date().toISOString(),
        text: action.payload,
        checked: false,
      });
      console.log(state.todos)
    },
    btnDelete(state, action) {},
    check(state, action) {},
  },
});

export const { addTodo, btnDelete, check } = slice.actions;
export default slice.reducer;
