import { Select, Input, SelectOnChange } from './blocks/form';
import { TYPES } from '../vars/vars';
import { useSelector } from 'react-redux';
import { FIRSTNAME_VALIDATION, MOBILE_VALIDATION, MODEL_VALIDATION } from '../vars/validations';
import { FIRSTNAME_ERROR, MOBILE_ERROR, MODEL_ERROR, SELECT_ERROR } from '../vars/errors';
import { useState } from 'react';

const RegistrationForm = () => {
  const selector = useSelector(state => state.todos.todos);

  const [firstname, setFirstname] = useState('');
  const [firstnameError, setFirstnameError] = useState(null);
  const [mobile, setMobile] = useState('');
  const [mobileError, setMobileError] = useState(null);
  const [model, setModel] = useState('');
  const [modelError, setModelError] = useState(null);
  const [type, setType] = useState('types');
  const [typeError, setTypeError] = useState(null);
  const [brand, setBrand] = useState('brands');
  const [brandError, setBrandError] = useState(null);

  const createBill = e => {
    e.preventDefault();
    !firstname.match(FIRSTNAME_VALIDATION) ? setFirstnameError(FIRSTNAME_ERROR) : setFirstnameError(null);
    !mobile.match(MOBILE_VALIDATION) ? setMobileError(MOBILE_ERROR) : setMobileError(null);
    !model.match(MODEL_VALIDATION) ? setModelError(MODEL_ERROR) : setModelError(null);
    type === 'types' ? setTypeError(SELECT_ERROR) : setTypeError(null);
    brand === 'brands' ? setBrandError(SELECT_ERROR) : setBrandError(null);

    console.log(firstnameError)
    // if (
    //   !firstnameError
    // ) {
    // const form = document.getElementById('registrationForm');
    // const formData = new FormData(form);
    // const formProps = Object.fromEntries(formData);
    // const json = JSON.stringify(formProps);
    // localStorage.setItem('bill', json);

    // document.getElementById('registrationForm').submit();
    // }
  };
  return (
    <section className='registration'>
      <h1>Live a request</h1>
      <form action='/form' method='GET' name='registrationForm' id='registrationForm'>
        <Input id='firstname' name='FirstName' type='text' actionField={setFirstname} actionError={firstnameError} />
        <Input id='mobile' name='Mobile' type='text' actionField={setMobile} actionError={mobileError} />
        <SelectOnChange name='types' types={TYPES} actionField={setType} actionError={typeError} />
        <Select types={selector} name='brands' actionField={setBrand} actionError={brandError} />
        <Input id='model' name='Model' type='text' actionField={setModel} actionError={modelError} />
        <input type='submit' onClick={e => createBill(e)} />
      </form>
    </section>
  );
};

export default RegistrationForm;
