import { useDispatch } from 'react-redux';

export const Input = props => {
  const { id, name, type } = props;
  return (
    <div className='input_block'>
      <label htmlFor={id}>{name}</label>
      <input type={type} name={id} id={id} className={id} />
      <div className={`${id}_error`}></div>
    </div>
  );
};

export const SelectOnChange = props => {
  const dispatch = useDispatch();
  const { types, name, getType } = props;
  return (
    <select name={name} id={name} onChange={e=>dispatch(getBrand(e.target.value))}>
      <option key='1' value={name} selected disabled>choose {name}</option>
      {types.map((el,id) => {
        return <option key={`${id}+1`} value={el}>{el}</option>;
      })}
    </select>
  );
};

export const Select = props => {
  const { types, name } = props;
  return (
    <select name={name} id={name}>
      <option key='1' value={name} selected disabled>choose {name}</option>
      {types.map((el,id) => {
        return <option key={`${id}+1`} value={el}>{el}</option>;
      })}
    </select>
  );
};
