export const checkInput = (name, validation, bockError, error) => {
  !firstname.match(FIRSTNAME_VALIDATION) ? setFirstnameError(FIRSTNAME_ERROR) : setFirstnameError(null);
};

export const checkSelect = (name, type, bockError, error) => {
  if (name.value === type) {
    bockError.textContent = error;
  } else {
    bockError.textContent = null;
  }
};
