import { createSlice } from '@reduxjs/toolkit';
import { BRAND_FRIDGE, BRAND_LAPTOP, BRAND_TVSET, BRAND_MOBILE } from '../';

const slice = createSlice({
  name: 'todoSlice',
  initialState: {
    todos: [],
    list: {}
  },
  reducers: {
    getBrand(state, action) {
      switch (action.payload) {
        case 'tv-set':
          state.todos = BRAND_TVSET;
          break;
        case 'fridge':
          state.todos = BRAND_FRIDGE;
          break;
        case 'laptop':
          state.todos = BRAND_LAPTOP;
          break;
        case 'mobile phone':
          state.todos = BRAND_MOBILE;
          break;
      }
    },
    getBill(state, action) {
      state.list = action.payload;
    },
  },
});

export const { getBrand, getBill } = slice.actions;
export default slice.reducer;
