import { createSlice } from '@reduxjs/toolkit';
import { BRAND_FRIDGE, BRAND_LAPTOP, BRAND_TVSET, BRAND_MOBILE } from '../vars/vars';

const productSlice = createSlice({
  name: 'Slice',
  initialState: {
    product: [],
    list: {},
  },
  reducers: {
    getBrand(state, action) {
      switch (action.payload) {
        case 'tv-set':
          state.product = BRAND_TVSET;
          break;
        case 'fridge':
          state.product = BRAND_FRIDGE;
          break;
        case 'laptop':
          state.product = BRAND_LAPTOP;
          break;
        case 'mobile phone':
          state.product = BRAND_MOBILE;
          break;
      }
    },
    getBill(state, action) {
      state.list = action.payload;
    },
  },
});

export const { getBrand, getBill } = productSlice.actions;
export default productSlice.reducer;
